import Head from 'next/head'
import Link from 'next/link'

export default function Home() {
  return (
    <div className="container">
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
        <div className="PageHeader">
          <h1 className="title">
            <a>Aman's Blog!</a>
          </h1>
          <div className="TopMenu">
            <ul>
              <li>
                  <Link href="/about">
                    <a>About Me</a>
                  </Link>
              </li>
              <li>
                  <Link href="/blog">
                    <a>Blog</a>
                  </Link>
              </li>
              <li>
                  <Link href="/contact">
                    <a>Contact Me</a>
                  </Link>
              </li>
            </ul>
          </div>
        </div>
      </main>
      <div className="grid">
          <a href="#" className="card">
            <img src="https://tinyurl.com/ydareqff" alt="BlogImage" className="BlogImage" />
            <h3>Why you should learn UI Design in 2021 &rarr;</h3>
            <p>Lorem ipsum dolor sitor orem ipsum dolor sitororem ipsum dolor sitor orem ipsum dolor sitororem ipsum dolor 
              sitor orem ipsum dolor sitororem ipsum dolor sitor orem ipsum dolor sitor </p>
          </a>


        </div>

      {/*
      <footer>
        <a
          href="https://vercel.com?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
          target="_blank"
          rel="noopener noreferrer"
        >
          Powered by{' '}
          <img src="/vercel.svg" alt="Vercel Logo" className="logo" />
        </a>
      </footer>
      */}

      <style jsx>{`

        .container {
          width: 100%;
        }

        main {
          display: flex;
          width: 100%;
        }

        .PageHeader {
          align-items: center;
          display: flex;
          margin-left: 5rem;
          width: 100%;
          padding-top: 5rem;
          height: 200px;
          justify-content: space-between;
        }

        .TopMenu {
          margin-right: 5rem;
        }

        .TopMenu > ul {
          list-style-type: none;
          margin: 0;
          padding: 0;
          overflow: hidden;
        }

        .TopMenu > ul > li {
          float: left;
          padding-right: 20px;
        }

        a {
          color: inherit;
          text-decoration: none;
        }

        .title a {
          color: #0070f3;
          text-decoration: none;
        }

        .title a:hover,
        .title a:focus,
        .title a:active {
          text-decoration: underline;
        }

        .title {
          margin: 0;
          line-height: 1.15;
          font-size: 4rem;
        }

        .title,
        .description {
          text-align: left;
        }

        .PageHeader > p {
          line-height: 1.5;
          font-size: 1.5rem;
        }

        code {
          background: #fafafa;
          border-radius: 5px;
          padding: 0.75rem;
          font-size: 1.1rem;
          font-family: Menlo, Monaco, Lucida Console, Liberation Mono,
            DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
        }

        .grid {
          display: flex;
          align-items: center;
          flex-wrap: wrap;
          max-width: 800px;
          margin-top: 3rem;
        }

        .card {
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .card:hover,
        .card:focus,
        .card:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .card h3 {
          margin: 0 0 1rem 0;
          font-size: 1.5rem;
        }

        .card p {
          margin: 0;
          font-size: 1.15rem;
          line-height: 1.5;
        }

        .BlogImage {
          width: 10%;
        }

        @media (max-width: 600px) {
          .grid {
            width: 100%;
            flex-direction: column;
          }
        }

      `}</style>

      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  )
}
